#!/bin/sh
grep -m 1 "^${1}->" ../Cycles/*.txt || grep -m 1 "^${1}->" ../Perfect/*.txt || grep -m 1 "^${1}->" ../Open/*/*/*.txt || grep -m 1 "^${1}->" ../NotOpen/*.txt ||
  grep -m 1 "^${1}->" ../Primes/0/*/*/*.txt || grep -m 1 "^${1}->" ../Primes/1/*/*/*.txt || grep -m 1 "^${1}->" ../Primes/2/*/*/*.txt || grep -m 1 "^${1}->" ../Primes/3/*/*/*.txt || grep -m 1 "^${1}->" ../Primes/4/*/*/*.txt || grep -m 1 "^${1}->" ../Primes/5/*/*/*.txt || grep -m 1 "^${1}->" ../Primes/6/*/*/*.txt || grep -m 1 "^${1}->" ../Primes/7/*/*/*.txt || grep -m 1 "^${1}->" ../Primes/8/*/*/*.txt || grep -m 1 "^${1}->" ../Primes/9/*/*/*.txt ||
  grep -m 1 "[[:digit:]]->${1}$" ../Primes/0/*/*/*.txt || grep -m 1 "[[:digit:]]->${1}$" ../Primes/1/*/*/*.txt || grep -m 1 "[[:digit:]]->${1}$" ../Primes/2/*/*/*.txt || grep -m 1 "[[:digit:]]->${1}$" ../Primes/3/*/*/*.txt || grep -m 1 "[[:digit:]]->${1}$" ../Primes/4/*/*/*.txt || grep -m 1 "[[:digit:]]->${1}$" ../Primes/5/*/*/*.txt || grep -m 1 "[[:digit:]]->${1}$" ../Primes/6/*/*/*.txt || grep -m 1 "[[:digit:]]->${1}$" ../Primes/7/*/*/*.txt || grep -m 1 "[[:digit:]]->${1}$" ../Primes/8/*/*/*.txt || grep -m 1 "[[:digit:]]->${1}$" ../Primes/9/*/*/*.txt ||
  grep -m 1 "[[:digit:]]->${1}$" ../Open/*/*/*.txt || grep -m 1 "\"i0: ${1}\"" ../Open/*/*/*.txt
